member.example:

// 使用示例：
List<MailMessage> s, f;		
MailMessageProvider mm = new MailMessageProvider();
mm.MailMessages.Add(mm.CreateMailMessage("111@htmlbird.net", "为测试 MailMessageProvider 类而发送的邮件", "这里是电子邮件的内容。"));
mm.MailMessages.Add(mm.CreateMailMessage("222@htmlbird.net", "为测试 MailMessageProvider 类而发送的邮件", "这里是电子邮件的内容。"));
mm.MailMessages.Add(mm.CreateMailMessage("333@htmlbird.net", "为测试 MailMessageProvider 类而发送的邮件", "这里是电子邮件的内容。"));
mm.MailMessages.Add(mm.CreateMailMessage("444@htmlbird.net", "为测试 MailMessageProvider 类而发送的邮件", "这里是电子邮件的内容。"));
mm.MailMessages.Add(mm.CreateMailMessage("555@htmlbird.net", "为测试 MailMessageProvider 类而发送的邮件", "这里是电子邮件的内容。"));
mm.SendAll(out s, out f);

Response.Write(String.Format("总共发送了 {0} 封电子邮件，其中 {1} 封发送成功，{2} 封发送失败。<br />", mm.MailMessages.Count, s.Count, f.Count));
